class Product {
    constructor(id, name, price, image) {
      this.id = id;
      this.name = name;
      this.price = price;
      this.image = image;
    }
  }
  
  class Cart {
    constructor() {
      this.items = [];
    }
  
    addItem(product) {
      this.items.push(product);
      this.updateCart();
    }
  
    removeItem(productId) {
      const itemIndex = this.items.findIndex(item => item.id === productId);
      if (itemIndex !== -1) {
        this.items.splice(itemIndex, 1);
      }
      this.updateCart();
    }
  
    getTotal() {
      return this.items.reduce((total, product) => total + product.price, 0).toFixed(2);
    }
  
    updateCart() {
      const cartItemsList = document.getElementById('cart-items');
      cartItemsList.innerHTML = ''; 
      
      this.items.forEach(item => {
        const li = document.createElement('li');
        li.classList.add('cart-item');
        
        li.innerHTML = `
          ${item.name} - $${item.price.toFixed(2)} 
          <button class="remove-item" onclick="removeFromCart(${item.id})">Remove</button>
        `;
        cartItemsList.appendChild(li);
      });
  
      const totalPrice = document.getElementById('total-price');
      totalPrice.textContent = this.getTotal();
    }
  }
  
  const products = [
    new Product(1, 'Asus Vivobook S15 OLED', 1205.09, 'https://th.bing.com/th/id/OIP.m_KK02bQ6aE8XlRR4Z7brgHaHa?rs=1&pid=ImgDetMain'),
    new Product(2, 'HXSJ S200 Wired USB Gaming Mouse', 8.03, 'https://th.bing.com/th/id/OIP.MK3czzjUgnzSR5sNAb5kaQHaHa?rs=1&pid=ImgDetMain'),
    new Product(3, 'SONY BRAVIA 65 Inch OLED', 5245.03, 'https://images.hometheaterreview.com/htr-stateless/2021/03/27a54437-6401203ld.jpg'),
    new Product(4, 'LG Smart Refrigirator', 1262.48, 'data:image/jpeg;base64,'),
    new Product(5, 'Apple Macbook Pro M3 Max', 2582.34, 'https://th.bing.com/th/id/OIP.CxhMNlye3efxtlMXlth5egHaHa?rs=1&pid=ImgDetMain'),
    new Product(6, 'One Plus 12', 746.01, 'data:image/jpeg;base64,'),
    new Product(7, 'One Plus Buds 3 Pro', 137.72, 'data:image/jpeg;base64,'),
    new Product(8, 'One Plus Watch 2', 300, 'data:image/jpeg;base64,')
];
  
  const cart = new Cart();
  
  function displayProducts() {
    const productContainer = document.getElementById('product-container');
    products.forEach(product => {
      const productDiv = document.createElement('div');
      productDiv.classList.add('product');
      
      productDiv.innerHTML = `
        <img src="${product.image}" alt="${product.name}">
        <h3>${product.name}</h3>
        <p>$${product.price.toFixed(2)}</p>
        <button onclick="addToCart(${product.id})">Add to Cart</button>
      `;
      
      productContainer.appendChild(productDiv);
    });
  }
  
  function addToCart(productId) {
    const product = products.find(p => p.id === productId);
    if (product) {
      cart.addItem(product);
    }
  }
  
  function removeFromCart(productId) {
    cart.removeItem(productId);
  }
  
  displayProducts();
  